INSERT INTO `nb1x_posts` (`ID`, `post_author`, `post_date`, `post_date_gmt`, `post_content`, `post_title`, `post_excerpt`, `post_status`, `comment_status`, `ping_status`, `post_password`, `post_name`, `to_ping`, `pinged`, `post_modified`, `post_modified_gmt`, `post_content_filtered`, `post_parent`, `guid`, `menu_order`, `post_type`, `post_mime_type`, `comment_count`) VALUES
(1, 1, '2016-11-09 14:03:31', '2016-11-09 14:03:31', 'Welcome to WordPress. This is your first post. Edit or delete it, then start blogging!', 'Hello world!', '', 'publish', 'open', 'open', '', 'hello-world', '', '', '2016-11-09 14:03:31', '2016-11-09 14:03:31', '', 0, 'http://localhost/smart-adas/?p=1', 0, 'post', '', 1),
(2, 1, '2016-11-09 14:03:31', '2016-11-09 14:03:31', '<strong>以主動式安全為訴求進行設計的 ADAS (Advanced Driver Assistance Systems)為汽車設計生產中非常重要的一系統，其主要利用感測器以及影像處理來輔助車輛駕駛者進行安全的駕駛，可以想見其重要性是不容忽視的。然而新式的汽車功能(如 ADAS)在汽車生產時，不論是產品的設計或測試大多以假設的情境和場域模型進行設計與測試,對於使用者長期使用的真實狀況與遭遇的問題，往往不可得知，也因此無法引導車廠衍生系統化產品長期不斷改善之方法。</strong>\r\n\r\n<strong>為了輔助產品設計與製程之優化,縮短優化所需產品週期，以符合生產力 4.0 產業需求與發展，本子計畫計畫研發一「輔助 ADAS 設計優化之基於知識本體發展之協同過濾推薦系統」，針對來自車輛即時產生的意外狀況、各設計生產單位人員的提問或回饋資訊,導入知識本體(Ontology)、文本探勘(Text Mining)、協同過濾(Collaborative Filtering)、機器學習(Machine Learning)等多項技術，自動化整理且學習產出最佳化解決方案的相關說明文稿或影像等多媒體資料的摘要整合資訊，推薦給相關設計生產單位人員參考，以協助車廠即時進行車輛設計生產的修正與改善，減少車輛意外發生的機率，讓大眾行車更安全更有保障，且可累積經驗知識自動化帶領各設計生產單位新手上工學習，讓車廠技術傳承與管理更進化，全面降低因處理車輛意外狀況所支出的成本與心力。</strong>', '', '', 'publish', 'open', 'open', '', 'about', '', '', '2016-11-19 02:34:48', '2016-11-19 02:34:48', '', 0, 'http://localhost/smart-adas/?page_id=2', 0, 'page', '', 0),
